import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { FormlyFieldConfig } from '@ngx-formly/core';
import { FormGroup } from '@angular/forms';

@Component({
  selector: 'app-counter-component',
  templateUrl: './counter.component.html',
})
export class CounterComponent {
  public fields: SectionFields[] = [];
 
  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<SectionFields[]>(baseUrl + 'SectionField?id=1').subscribe(result => {
      this.fields = result;
    }, error => console.error(error));
  }
  form = new FormGroup({});
  model = {};
  fields2: FormlyFieldConfig[] = [
    {
      "key": "header",
      "type": "input",
      "templateOptions": {
        "type": "text",
        "label": "Ilość pakowanego piwa",
        "placeholder": "Podaj Ilość pakowanego piwa",
        "required": true
      }
    },
    {
      "key": "header",
      "type": "input",
      "templateOptions": {
        "type": "text",
        "label": "Poziom CO2",
        "placeholder": "Podaj Poziom CO2",
        "required": true
      }
    },
    {
      "key": "header",
      "type": "input",
      "templateOptions": {
        "type": "text",
        "label": "Temp. piwa",
        "placeholder": "Podaj Temp. piwa",
        "required": true
      }
    }
  ];

  onSubmit(model: any) {
    console.log(model);
  }
}

interface SectionFields {
  key: string;
  type: string;
  templateOptions: TemplateOptions;
}
interface TemplateOptions {
  type: string;
  label: string;
  placeholder: string;
  required: boolean;
}
<h1>Counter</h1>

<p>This is a simple example of an Angular component.</p>

<p aria-live="polite">Current count: <strong></strong></p>

<form [formGroup]="form" (ngSubmit)="onSubmit(model)">
  <formly-form [form]="form" [fields]="fields2" [model]="model"></formly-form>
  <button type="submit" class="btn btn-default">Submit</button>
</form>
