import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { NgcCookieConsentModule } from 'ngx-cookieconsent';
import { BrowserModule } from '@angular/platform-browser';
import { NgModule } from '@angular/core';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { HttpClientModule } from '@angular/common/http';
import { RouterModule } from '@angular/router';
import { CommonModule } from '@angular/common';
import { FormlyModule } from '@ngx-formly/core';
import { FormlyBootstrapModule } from '@ngx-formly/bootstrap';
import { FormlyFieldConfig } from '@ngx-formly/core';
import { FormGroup } from '@angular/forms';
import { ActivatedRoute, ParamMap, Router } from '@angular/router';
import { Inject } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {
  readonly ROOT_URL = 'http://localhost:5069/';

  message: any;
  cookies: any

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<MOTD>(this.ROOT_URL + 'MOTD').subscribe(result => {
      this.message = result;
    }, error => console.error(error));
    http.get<Cookies>(this.ROOT_URL + 'Cookies').subscribe(result => {
      this.cookies = result;
    }, error => console.error(error));
  }


  ngOnInit() {
    let cc = window as any;
    cc.cookieconsent.initialise({
      palette: {
        popup: {
          background: "#164969"
        },
        button: {
          background: "#ffe000",
          text: "#164969"
        }
      },
      theme: "classic",
      content: {
        message: this.cookies.message,
        dismiss: this.cookies.dismiss,
        link: this.cookies.link,
        href: this.cookies.href
      }
    });
  }
}

interface MOTD {
  message: string;
}
interface Cookies {
  message: any;
  dismiss: any;
  link: any;
  href: any;
}
